INSERT INTO `system_log` VALUES ('789', '127.0.0.1', 'blog/article/edit', 'admin', 'blog/article/edit', '{\"id\":\"16\",\"title\":\"Linux下php安装Redis扩展\",\"isfabu\":\"1\",\"content\":\"<p>&nbsp;<\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">注意：目录的权限 &nbsp; chomd 777 -R<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">&nbsp;<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">1、安装redis<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">下载：<\\/span><a href=\\\"https:\\/\\/github.com\\/nicolasff\\/phpredis\\/archive\\/2.2.4.tar.gz\\\"><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(26, 139, 200);\\\">https:\\/\\/github.com\\/nicolasff\\/phpredis\\/archive\\/2.2.4.tar.gz<\\/span><\\/a><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">上传phpredis-2.2.4.tar.gz到\\/usr\\/local\\/src目录<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">cd \\/usr\\/local\\/src&nbsp;#进入软件包存放目录<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">tar zxvf phpredis-2.2.4.tar.gz&nbsp;#解压<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">cd phpredis-2.2.4&nbsp;#进入安装目录<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">\\/usr\\/local\\/php\\/bin\\/phpize&nbsp;#用phpize生成configure配置文件<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">.\\/configure --with-php-config=\\/usr\\/local\\/php\\/bin\\/php-config&nbsp;&nbsp;#配置<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">make&nbsp;&nbsp;#编译<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">make install&nbsp;&nbsp;#安装<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">安装完成之后，出现下面的安装路径<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">\\/usr\\/local\\/php\\/lib\\/php\\/extensions\\/no-debug-non-zts-20090626\\/<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">2、配置php支持<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">vi \\/usr\\/local\\/php\\/etc\\/php.ini&nbsp;&nbsp;#编辑配置文件，在最后一行添加以下内容<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">添加<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">extension=&quot;redis.so&quot;<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">:wq!&nbsp;#保存退出<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">&nbsp;<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">&nbsp;<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">3 &nbsp;重启服务<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">sudo service nginx restart<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 13px; font-family: Verdana; color: rgb(75, 75, 75);\\\">sudo \\/etc\\/init.d\\/php-fpm restart<\\/span><\\/p>\\n\\n<p>&nbsp;<\\/p>\\n\",\"tags\":\"\",\"category\":\"19\",\"thumb\":\"\\/static\\/upload\\/99033006d613dbc0\\/1797b5bac4b7ed87.jpg\",\"laiyuan\":\"\"}', '', '2019-01-06 16:42:17');
