INSERT INTO `system_log` VALUES ('794', '127.0.0.1', 'blog/article/edit', 'admin', 'blog/article/edit', '{\"id\":\"18\",\"title\":\"linux下出现 The server quit without updating PID file\",\"isfabu\":\"1\",\"content\":\"<p><span style=\\\"font-size: 16px;\\\"><span style=\\\"font-family: &quot;Microsoft YaHei&quot;, STXihei; font-size: 14px;\\\">linux下出现 The server quit without updating PID file 出现解决方法 把\\/usr\\/local\\/mysql\\/var\\/目录下的mysql-bin.index删掉就好了<\\/span><\\/span><\\/p>\\n\",\"tags\":\"\",\"category\":\"19\",\"thumb\":\"http:\\/\\/127.0.0.1\\/static\\/upload\\/0b7d971bc3a2fdc1\\/bbf4ee6e64ca35d4.jpg\",\"laiyuan\":\"\"}', '', '2019-01-06 16:43:03');
