INSERT INTO `system_log` VALUES ('820', '127.0.0.1', 'blog/article/edit', 'admin', 'blog/article/edit', '{\"id\":\"27\",\"title\":\"mongodb的增删改查\",\"isfabu\":\"1\",\"content\":\"<p><span style=\\\"font-size: 22px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251); font-weight: bold; font-style: italic; text-decoration: underline;\\\">shell命令<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 16px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251); font-weight: bold; font-style: italic; text-decoration: underline;\\\">插入<\\/span><span style=\\\"font-size: 22px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251); font-weight: bold; font-style: italic; text-decoration: underline;\\\">：<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">db<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">.<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">runoob<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">.<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">insert<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">({<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(0, 136, 0); background-color: rgb(251, 251, 251);\\\">&quot;name&quot;<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">:<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(0, 136, 0); background-color: rgb(251, 251, 251);\\\">&quot;菜鸟教程&quot;<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">}) <\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">runoob为集合的名字 <\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">删除：<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(51, 51, 51); background-color: rgb(251, 251, 251);\\\">db.dropDatabase() &nbsp;默认删除当前数据库 &nbsp;db命令查看当前数据库<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 19px; font-family: &quot;Open Sans&quot;; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-weight: bold;\\\">删除集合：<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">db<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">.<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">collection<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">.<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">drop<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">()<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">文档数据存储格式：<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Microsoft YaHei&quot;, STXihei; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\\\">BSON是一种类json的一种二进制形式的存储格式,简称Binary JSON。<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 12px; font-family: &quot;Microsoft YaHei&quot;, STXihei; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\\\">MongoDB 使用 insert() 或 save() 方法向集合中插入文档<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">db<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">.<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">COLLECTION_NAME<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">.<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">insert<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">(<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">document<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">) &nbsp;<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">document为bson格式<\\/span><\\/p>\\n\\n<p><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">db<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; color: rgb(102, 102, 0); background-color: rgb(251, 251, 251);\\\">.<\\/span><span style=\\\"font-size: 12px; font-family: &quot;Courier New&quot;; background-color: rgb(251, 251, 251);\\\">col<\\/span><span styl