INSERT INTO `system_log` VALUES ('826', '127.0.0.1', 'blog/article/edit', 'admin', 'blog/article/edit', '{\"id\":\"29\",\"title\":\"远程连接mysql 创建用户并赋予权限\",\"isfabu\":\"1\",\"content\":\"<p><span style=\\\"background-color: rgb(221, 237, 251); font-family: tahoma, arial, 宋体;\\\">CREATE USER demo IDENTIFIED BY &ldquo;123456&rdquo;<span style=\\\"background-color: inherit;\\\">&nbsp;<\\/span><\\/span><\\/p>\\n\\n<p><span style=\\\"background-color: rgb(221, 237, 251); font-family: tahoma, arial, 宋体;\\\"><span style=\\\"background-color: inherit;\\\">GRANT ALL PRIVILEGES ON *.* TO &#39;demo&#39;@&#39;%&#39;WITH GRANT OPTION<span style=\\\"background-color: inherit;\\\">&nbsp;<\\/span><\\/span><\\/span><\\/p>\\n\\n<p><span style=\\\"background-color: rgb(221, 237, 251); font-family: tahoma, arial, 宋体;\\\"><span style=\\\"background-color: inherit;\\\"><span style=\\\"background-color: inherit;\\\">FLUSH PRIVILEGES<span style=\\\"background-color: inherit;\\\">&nbsp;<\\/span><\\/span><\\/span><\\/span><\\/p>\\n\\n<p>&nbsp;<\\/p>\\n\",\"tags\":\"\",\"category\":\"17\",\"thumb\":\"\\/static\\/upload\\/714b57922c3fbd63\\/6987bb9c1ddf3f6a.jpg\",\"laiyuan\":\"\"}', '', '2019-01-06 16:45:53');
