INSERT INTO `system_log` VALUES ('828', '127.0.0.1', 'admin/plugs/upload', 'admin', 'admin/plugs/upload', '{\"OSSAccessKeyId\":\"\",\"success_action_status\":\"200\",\"id\":\"WU_FILE_0\",\"name\":\"034d54fb7a407604c2384ed4caa4481c.jpg\",\"type\":\"image\\/jpeg\",\"lastModifiedDate\":\"Sat Jan 20 2018 16:37:56 GMT+0800 (中国标准时间)\",\"size\":\"24286\",\"allowed_types\":\"jpg|png|jpeg\",\"token\":\"cf698cffc7da42799ffdf9c00e7db4d6\",\"md5\":\"034d54fb7a407604c2384ed4caa4481c\",\"key\":\"034d54fb7a407604\\/c2384ed4caa4481c.jpg\"}', '', '2019-01-06 16:46:11');
INSERT INTO `system_log` VALUES ('829', '127.0.0.1', 'blog/article/edit', 'admin', 'blog/article/edit', '{\"id\":\"30\",\"title\":\"php使用phpmail类库发送邮件\",\"isfabu\":\"1\",\"content\":\"<p><span style=\\\"font-size: 26px; color: rgb(255, 0, 0); line-height: 1.5;\\\">实测例子--》成功<\\/span><\\/p>\\n\\n<p>&nbsp;<\\/p>\\n\\n<pre class=\\\"brush:php;toolbar:false\\\">\\nfunction&nbsp;sendMail($to,&nbsp;$name,&nbsp;$subject&nbsp;=&nbsp;&#39;&#39;,&nbsp;$body&nbsp;=&nbsp;&#39;&#39;,&nbsp;$attachment&nbsp;=&nbsp;null){\\n&nbsp;&nbsp;&nbsp;$config=&nbsp;array(\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;SMTP_HOST&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;smtp.163.com&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/SMTP服务器\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;SMTP_PORT&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;465&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/SMTP服务器端口......端口为465才需要使用ssl协议，如果是25&nbsp;就不需要加&nbsp;$mail-&gt;SMTPSecure&nbsp;=&nbsp;&#39;ssl&#39;;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;SMTP_USER&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;18962294385@163.com&#39;,&nbsp;\\/\\/SMTP服务器用户名\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;SMTP_PASS&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;******&#39;,&nbsp;\\/\\/SMTP服务器密码\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;FROM_EMAIL&#39;&nbsp;&nbsp;=&gt;&nbsp;&#39;18962294385@163.com&#39;,&nbsp;\\/\\/发件人EMAIL\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;FROM_NAME&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;18962294385&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/发件人名称\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;REPLY_EMAIL&#39;&nbsp;=&gt;&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/回复EMAIL（留空则为发件人EMAIL）\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;REPLY_NAME&#39;&nbsp;&nbsp;=&gt;&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/回复名称（留空则为发件人名称）\\n&nbsp;&nbsp;&nbsp;);\\n&nbsp;&nbsp;&nbsp;require&nbsp;__DIR__.&#39;\\/phpmailer\\/PHPMailerAutoload.php&#39;;\\n&nbsp;&nbsp;&nbsp;$mail&nbsp;=&nbsp;new&nbsp;PHPMailer();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/PHPMailer对象\\n&nbsp;&nbsp;&nbsp;$mail-&gt;CharSet&nbsp;=&nbsp;&#39;UTF-8&#39;;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/设定邮件编码，默认ISO-8859-1，如果发中文此项必须设置，否则乱码\\n&nbsp;&nbsp;&nbsp;$mail-&gt;IsSMTP();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/&nbsp;设定使用SMTP服务\\n&nbsp;&nbsp;&nbsp;$mail-&gt;SMTPDebug&nbsp;&nbsp;=&nbsp;1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/&nbsp;关闭SMTP调试功能\\n&nbsp;&nbsp;&nbsp;\\/\\/&nbsp;1&nbsp;=&nbsp;errors&nbsp;and&nbsp;messages\\n&nbsp;&nbsp;&nbsp;\\/\\/&nbsp;2&nbsp;=&nbsp;messages&nbsp;only\\n&nbsp;&nbsp;&nbsp;$mail-&gt;SMTPAuth&nbsp;&nbsp;&nbsp;=&nbsp;true;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/&nbsp;启用&nbsp;SMTP&nbsp;验证功能\\n&nbsp;&nbsp;&nbsp;if($config[&#39;SMTP_PORT&#39;]&nbsp;!=&nbsp;25){\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;SMTPSecure&nbsp;=&nbsp;&#39;ssl&#39;;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\/\\/&nbsp;使用安全协议&nbsp;......端口为465才需要使用ssl协议，如果是25&nbsp;就不需要加&nbsp;$mail-&gt;SMTPSecure&nbsp;=&nbsp;&#39;ssl&#39;;\\n\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;Host&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;$config[&#39;SMTP_HOST&#39;];&nbsp;&nbsp;\\/\\/&nbsp;SMTP&nbsp;服务器\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;Port&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;$config[&#39;SMTP_PORT&#39;];&nbsp;&nbsp;\\/\\/&nbsp;SMTP服务器的端口号\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;Username&nbsp;&nbsp;&nbsp;=&nbsp;$config[&#39;SMTP_USER&#39;];&nbsp;&nbsp;\\/\\/&nbsp;SMTP服务器用户名\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;Password&nbsp;&nbsp;&nbsp;=&nbsp;$config[&#39;SMTP_PASS&#39;];&nbsp;&nbsp;\\/\\/&nbsp;SMTP服务器密码\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;SetFrom($config[&#39;FROM_EMAIL&#39;],&nbsp;$config[&#39;FROM_NAME&#39;]);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$replyEmail&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;$config[&#39;REPLY_EMAIL&#39;]?$config[&#39;REPLY_EMAIL&#39;]:$config[&#39;FROM_EMAIL&#39;];\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$replyName&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;$config[&#39;REPLY_NAME&#39;]?$config[&#39;REPLY_NAME&#39;]:$config[&#39;FROM_NAME&#39;];\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;AddReplyTo($replyEmail,&nbsp;$replyName);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;Subject&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;$subject;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;MsgHTML($body);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$mail-&gt;AddAddress($to,&nbsp;$name);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(is_array($attachment)){&nbsp;\\/\\/&nbsp;添加附件\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;($attachment&nbsp;as&nbsp;$file){\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;is_file($file)&nbsp;&amp;&amp;&nbsp;$mail-&gt;AddAttachment($file,$file);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$mail-&gt;Send()?&nbsp;true&nbsp;:&nbsp;false;\\n&nbsp;&nbsp;&nbsp;}\\n}<\\/pre>\\n\\n<pre style=\\\"background-color:#272822;color:#f8f8f2;font-family:\'Consolas\';font-size:15.0pt;\\\">\\nsendMail(&#39;1922884595@qq.com&#39;,123,&#39;eeeee&#39;,\\n&#39;fdsfds&#39;.&#39;&lt;a&nbsp;href=&quot;&nbsp;\\nalert(&quot;fdfd&quot;);&lt;\\/script&gt;&#39;,array(&quot;D:\\/WWW\\/test\\/server.php&quot;,\\n&quot;D:\\/WWW\\/test\\/readme.md&quot;));<\\/pre>\\n\\n<p>&nbsp;<\\/p>\\n\",\"tags\":\"\",\"category\":\"5\",\"thumb\":\"\\/static\\/upload\\/034d54fb7a407604\\/c2384ed4caa4481c.jpg\",\"laiyuan\":\"\"}', '', '2019-01-06 16:46:16');
